sp_widget.client_script:
api.controller = function($scope, $location, $timeout) {
  /* widget controller */
  var c = this;

	c.activeTab = $location.hash() || 'you';
	c.isLoading = true; // Start in a faded state for the load-in animation

	c.data.recognitions = c.data.recognitions.map(function(recognition) {
		var senderInitials = recognition.senderDisplay.split(' ').map(function(namePart) {
			return namePart[0].toUpperCase();	
		}).join('');

		return {...recognition, timeAgo: convertToTimeAgo(recognition.createdOn), senderInitials: senderInitials};
	});

	// Filter recognitions onload based on tab
	c.data.filteredRecognitions = getFilteredRecognitions(c.data.recognitions, c.activeTab);


	// Controller methods
	c.setTab = function(tabName) {
		if (c.activeTab === tabName) return;

		$location.hash(tabName);
	};


	// Even listeners
	// listen for back/forward navigation, switch to the tab as per hash
  $scope.$on('$locationChangeSuccess', function() {
    var newHash = $location.hash();

		if (!newHash || newHash == c.activeTab) return;

		// Start fade animation
		c.isLoading = true;

		c.activeTab = newHash;

		$timeout(function() {
			// Filter recognitions on tab change
			c.data.filteredRecognitions = getFilteredRecognitions(c.data.recognitions, c.activeTab);

			// Stop fade animation
			c.isLoading = false;
		}, 400);

  });

	// Triggers the initial fade-in animation when the feed first loads.
	$timeout(function() {
    c.isLoading = false;
  }, 400);

	// Helper methods
	function getFilteredRecognitions(recognitions, currentTab) {
		if (currentTab === 'all') return recognitions;


		return recognitions.filter((rec) => rec.feed.includes(currentTab));
	}

	function convertToTimeAgo(dateTime) {
		if (!dateTime) return '';

		moment.updateLocale('en', {
			relativeTime: {
				future: "in %s",
				past:   "%s ago",
				s:      "a few seconds", 
				ss:     "%d seconds",    
				m:      "a minute",      
				mm:     "%d minutes",
				h:      "an hour",      
				hh:     "%d hours",
				d:      "a day",      
				dd:     "%d days",
				M:      "a month",    
				MM:     "%d months",
				y:      "a year",      
				yy:     "%d years"
			}
		});

		return moment(dateTime).fromNow();
	}
};

