sp_widget.script:
(function() {

	var limit = options.record_limit || 10;

	var recognitionDAO = new x_1794402_peerce_0.RecognitionDAO();

	data.recognitions = recognitionDAO.getRecognitions(limit);


	var analyticsRoles = ['admin', 'x_1794402_peerce_0.admin', 'x_1794402_peerce_0.manager', 'x_1794402_peerce_0.hr_analyst'];

	data.showAnalytics = false;

	for (var i = 0; i < analyticsRoles.length; i++) {
		if (gs.hasRole(analyticsRoles[i])) {
			data.showAnalytics = true;
			break;
		}
	}

})();
