sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    let eventName = '';

    const interactionType = current.getValue('type');
    // Alert for recognition likes
    if (current.parent.nil() && interactionType == 'reaction') {
        eventName = 'x_1794402_peerce_0.recognition_liked';
    } else if (current.parent.nil() && interactionType == 'comment') { // Alert for recognition comments
        eventName = 'x_1794402_peerce_0.comment_added';
    }

    if (!eventName) return;

    const interactionUser = current.getValue('user');
    const recognitionId = current.getValue('recognition');

    // Query recognition recipients and trigger the alert notification for them
    const grRecognitionRecipient = new GlideRecord('x_1794402_peerce_0_recognition_recipient');
    grRecognitionRecipient.addQuery('recognition', recognitionId);
    grRecognitionRecipient.addQuery('recognition.is_team', false);
    // Exclude the interaction user if he's one of the recipients
    grRecognitionRecipient.addQuery('recipient_user', '!=', interactionUser);
    grRecognitionRecipient.query();

    while (grRecognitionRecipient.next()) {
        gs.eventQueue(eventName, current, grRecognitionRecipient.getValue('recipient_user'));
    }

})(current, previous);
