sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    var isPrivate = current.getElement('recognition.is_private').getValue();

    var managerEmail;
    var managerName;

    if (current.getValue('recipient_user')) {
        managerEmail = current.getDisplayValue('recipient_user.manager.email');
        managerName = current.getDisplayValue('recipient_user.manager');
    } else if (current.getValue('recipient_group')) {
        managerEmail = current.getDisplayValue('recipient_group.manager.email');
        managerName = current.getDisplayValue('recipient_group.manager');
    }

    if (managerEmail && isPrivate == '0') {
        email.addAddress('cc', managerEmail, managerName);
    }

    var notifyToUserIds = current.getElement('recognition.notify_to').getValue();

    if (notifyToUserIds) {
        var grUser = new GlideRecord('sys_user');
        grUser.addQuery('sys_id', 'IN', notifyToUserIds);
        grUser.addNotNullQuery('email');
        grUser.query();

        while (grUser.next()) {
            email.addAddress('cc', grUser.getValue('email'), grUser.getDisplayValue('name'));
        }
    }

})(current, template, email, email_action, event);
