sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Add your code here

    var occasions = [];
    var grRecognitionOccasions = new GlideRecord('x_1794402_peerce_0_applied_recognition_occasion');
    grRecognitionOccasions.addQuery('recognition', current.getValue('recognition'));
    grRecognitionOccasions.query();

    while (grRecognitionOccasions.next()) {
        occasions.push(grRecognitionOccasions.getValue('occasion_text'));
    }

    var occasionDisplay = occasions.toString();
    if (occasions.length > 1) {
        occasionDisplay = occasions.slice(0, occasions.length - 1).join(', ') + ', and ' + occasions[occasions.length - 1];
    }

    template.print('<em>' + occasionDisplay + '</em>');

})(current, template, email, email_action, event);
