sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
  /* Optional EmailOutbound */
  email, /* Optional GlideRecord */ email_action,
  /* Optional GlideRecord */
  event) {

  var recognitionURL = gs.getProperty('glide.servlet.uri') + 'pc?recognition_id=' + current.getValue('recognition') + '#all';

  var recognitionLink = '<a href="' + recognitionURL + '" target="_blank" rel="noopener noreferrer nofollow" style="font-size: 16px; font-family: Arial, sans-serif; font-weight: bold; color: #ffffff; text-decoration: none; padding: 15px 30px; display: inline-block;">View in PeerCentral </a>';

  template.print(recognitionLink);

})(current, template, email, email_action, event);
