sys_script_include.script:
var RecognitionUtils = Class.create();
RecognitionUtils.prototype = {
    initialize: function() {
        this.teamMembers = this.getUserTeamMembers();
    },

    isTeamRecognition: function(sender, recipients) {
        if (!this.teamMembers) return false;

        if (this.teamMembers.includes(sender)) return true;

        return recipients.some(recipient => this.teamMembers.includes(recipient));
    },

    getUserTeamMembers: function(userId) {
        userId = userId || gs.getUserID();

        var userGroups = this.getUserGroups(userId);
        var teamMembers = [];

        var grGroupMember = new GlideRecord('sys_user_grmember');
        grGroupMember.addQuery('group', 'IN', userGroups.toString());
        grGroupMember.query();

        while (grGroupMember.next()) {
            teamMembers.push(grGroupMember.user.toString());
        }

        return new global.ArrayUtil().unique(teamMembers);
    },

    getUserGroups: function(userId) {
        userId = userId || gs.getUserID();

        var userGroups = [];

        var grGroupMember = new GlideRecord('sys_user_grmember');
        grGroupMember.addQuery('user', userId);
        grGroupMember.addQuery('group.active', true);
        grGroupMember.query();

        while (grGroupMember.next()) {
            userGroups.push(grGroupMember.group.toString());
        }

        return userGroups;
    },

    type: 'RecognitionUtils'
};
